import { useWallet, useConnection} from '@solana/wallet-adapter-react';
import { TOKEN_PROGRAM_ID} from "@solana/spl-token";
import TokenCount from './TokenCount';
import Button from './Button';
import {Link} from 'react-router-dom';

const MINT_ADD = "6rbtgebh89nx2vd9EbzcTMhQS8pKUG9ggoGEijuMFUbC";

export const WalletInspector = ({onClick}) => {
    const { connection } = useConnection();
    const { publicKey } = useWallet();
    let my_count = 0;

    async function getBalance() {
        const tokens = await connection.getParsedTokenAccountsByOwner(publicKey, {programId: TOKEN_PROGRAM_ID});
        console.log(tokens);
        let shein_token = tokens.find(t => t.account.data.parsed.info.mint === MINT_ADD);
        return shein_token ? shein_token.account.data.parsed.info.tokenAmount.uiAmount : 0;

    }
    if (publicKey){
        my_count = getBalance();
    }

    return (
        <div style={{textAlign: "center"}}>
            {publicKey && my_count > 0 &&
                <><TokenCount count={my_count} /><Link to='/destination'>
                    <Button text="Enter Portal..." color="Green" />
                </Link></>
            }
        </div>

    );
};

export default WalletInspector;
