import { useWallet, useConnection} from '@solana/wallet-adapter-react';
import { TOKEN_PROGRAM_ID} from "@solana/spl-token";
import TokenCount from './TokenCount';
import Button from './Button';
import {Link} from 'react-router-dom';

const MINT_ADD = "6rbtgebh89nx2vd9EbzcTMhQS8pKUG9ggoGEijuMFUbC";

export const WalletInspector = ({onClick}) => {
    const { connection } = useConnection();
    const { publicKey } = useWallet();
    let my_count = 0;

    if (publicKey){
        const tokens = connection.getParsedTokenAccountsByOwner(publicKey, {programId: TOKEN_PROGRAM_ID});
        my_count = 1;
    }

    return (
        <div style={{textAlign: "center"}}>
            <TokenCount count={my_count}/>
            {publicKey && my_count > 0 && 
            <Link to='/destination'>
            <Button text="Enter Portal..." color="Green"/>
            </Link>}
        </div>

    );
};

export default WalletInspector;
